import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { ItemServiceService } from 'src/app/services/item-service.service';
import { SellerService } from 'src/app/services/seller.service';

@Component({
  selector: 'app-seller-dashboard',
  templateUrl: './seller-dashboard.component.html',
  styleUrls: ['./seller-dashboard.component.scss']
})
export class SellerDashboardComponent implements OnInit {
itemList:any;
updateProduct : FormGroup;
updatedID:any;

updateOBJ:any = {
  item_name: '',
  item_price: '',
  item_discount: '',
  item_dsc: '',
  item_qty: '',
  item_image: '',
}

constructor(private sellerSer:SellerService, private IS:ItemServiceService, private router:Router, private fb:FormBuilder, private dailog:MatDialog ){
  this.updateProduct = this.fb.group({
    item_name : this.fb.control('',[Validators.required]),
    item_price : this.fb.control('',[Validators.required]),
    item_discount : this.fb.control('',[Validators.required]),
    item_qty : this.fb.control('',[Validators.required]),
    item_image : this.fb.control('',[Validators.required]),
    item_dsc : this.fb.control('',[Validators.required]),
  })
}

ngOnInit(): void {
  this.checkSeller();
  this.getItems();
}

checkSeller(){
  let seller = localStorage.getItem("seller");
  let sellerVerify = seller && JSON.parse(seller).data.id
  if(sellerVerify){
 this.sellerSer.sellerActive.emit(true);
}
}



getItems() {
  this.IS.getItems().subscribe((res: any) => {
    this.itemList = res.Result;
  })
}

UpdatePro(item:any, UpdateProduct:any){
  this.updatedID = item.id;
  this.updateOBJ =  item;
this.dailog.open(UpdateProduct,{
  width:'550px'
})
console.log(this.updateOBJ,item,"itemmm");


}


ProductUpdate(value:any){


this.sellerSer.updateProduct(this.updatedID,value).subscribe((res:any)=>{
  console.log(res,"resss");

})
}
}
<div class="container mt-4">
  <div class="row d-flex justify-content-center m-2">


    <div class="col-6 col-sm-6 col-lg-3 mb-2 mt-2 p-1" *ngFor="let item of itemList">
      <div class="card" style="height: 350px;">
        <img src="{{ item.item_image }}" class="card-img-top img-fluid" alt="..." style="height: 200px;">
        <div class="card-body text-center">
          <h5 class="card-title">{{ item.item_name }}</h5>
          <div class="content mt-1 mb-4" style="height: 20px;">

            <p class="card-text mb-4 text-danger">Discount {{ item.item_discount }}%</p>
          </div>
          <div class="btnAdd d-flex justify-content-between" style="margin-top: 20px;">
            <div class="aadto">
              <a class="btn btn-dark" (click)="UpdatePro(item, UpdateProduct)">Edit Product</a>
            </div>

            <div class="price ">
              <p class="text-success mt-2 m-1"> ₹ {{ item.item_price }}</p>
            </div>

          </div>
        </div>
      </div>
    </div>



  </div>
</div>

<ng-template #UpdateProduct>
  <div class="container">
    <div class="row d-flex justify-content-center">
      <div class="col-12 mt-5 mb-5">
        <form [formGroup]="updateProduct" (ngSubmit)="ProductUpdate(updateProduct.value)">

          <div class="formInput mt-2 mb-2">
            <label for="">Item Name</label>
            <input type="text" formControlName="item_name" id="" class="form-control" [value]="updateOBJ.item_name"
              [(ngModel)]="updateOBJ.item_name">
          </div>

          <div class="formInput mt-2 mb-2">
            <label for="">Price</label>
            <input type="text" formControlName="item_price" id="" class="form-control" value="{{updateOBJ.item_price}}"
              [(ngModel)]="updateOBJ.item_price">
          </div>

          <div class="formInput mt-2 mb-2">
            <label for="">Description</label>
            <input type="text" formControlName="item_dsc" id="" class="form-control" value="{{updateOBJ.item_dsc}}"
              [(ngModel)]="updateOBJ.item_dsc">
          </div>

          <!-- <div class="formInput mt-2 mb-2">
            <label for="">Quantity</label>
            <input type="number" formControlName="item_qty" id="" class="form-control" value="{{updateOBJ.item_qty}}" [(ngModel)]="updateOBJ.item_qty" [disabled]="true">
          </div> -->

          <div class="formInput mt-2 mb-2">
            <label for="">Image URL</label>
            <input type="text" formControlName="item_image" id="" class="form-control" value="{{updateOBJ.item_image}}"
              [(ngModel)]="updateOBJ.item_image">
          </div>

          <div class="formInput mt-2 mb-2">
            <label for="">Discount</label>
            <input type="number" formControlName="item_discount" id="" class="form-control"
              value="{{updateOBJ.item_name}}" [(ngModel)]="updateOBJ.item_discount">
          </div>


.r

          <div class="loginBtn mt-4 d-flex justify-content-between">
            <button class="btn btn-dark col-4 col-sm-4">Update Product</button>
          </div>


          <div class="loginBtn mt-4 d-flex justify-content-between">
            <button class="btn btn-dark col-4 col-sm-4">Delete Product</button>
          </div>

        </form>
      </div>
    </div>
  </div>

</ng-template>
