import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-seller',
  templateUrl: './seller.component.html',
  styleUrls: ['./seller.component.scss']
})
export class SellerComponent {
  sellerLogin : FormGroup;

  constructor(private fb: FormBuilder){
    this.sellerLogin = this.fb.group({
email: this.fb.control('',[]),
password : this.fb.control('',[]),
    });
  }



  lo(value: any) {
    if (this.loginForm.valid) {
      this.userS.userLogin(value);
    }
  }

  // checkUser() {
  //   let user: any = localStorage.getItem("user");
  //   let UserId = user && JSON.parse(user).data.id;
  //   if (UserId) {
  //   this.item.ActiveUser.emit(true);
  //   }
  // }

  emitUser() {
    let user = localStorage.getItem("user");
    let userVerify = user && JSON.parse(user).data.id
    if(userVerify){
   this.item.ActiveUser.emit(true);
  }
  }



}
<div class="container">
  <div class="row d-flex justify-content-center">
    <div class="col-8">

      <div class="seller-login mt-4 mb-3">
<h2 class="text-center">Seller Login</h2>
        <form [formGroup]="sellerLogin" (ngSubmit)="loginSeller(sellerLogin.value)">

          <div class="form-group mt-2 mb-2">
  <input type="email" class="form-control" formControlName="email" placeholder="Enter Seller Email Here...">
</div>



<div class="form-group mt-2 mb-2">
  <input type="password" class="form-control" formControlName="password" placeholder="Enter Seller Password Here...">
</div>

<div class="sellerLoginBtn text-center mt-3 mb-3" >
  <button class="btn btn-info"> Login as seller</button>
</div>

</form>
      </div>
    </div>
  </div>
</div>
