import { Component, HostListener, Injectable, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ItemServiceService } from 'src/app/services/item-service.service';
import { SellerService } from 'src/app/services/seller.service';
import { ToasterService } from 'src/app/services/toaster.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})


export class HeaderComponent implements OnInit {
  sellerActive:any;
 ActiveUser:any;
  cartCount: number = 0;
  bgColor = 'transparent'; // Initial background color
  path:
  constructor(private itemSer: ItemServiceService, private route:Router, private toaster:ToasterService, private sellerSer:SellerService) { }

  ngOnInit() {
    this.cartItemsCount();
    this.checkUser();
    this.emitUser();
    this.checkSeller();
    this.checkSellerActive();
  }

  @HostListener('window:scroll', ['$event'])
  onScroll(event: any) {
    if (window.pageYOffset > 50) {
      this.bgColor = '#ffff'; // Change background color after scrolling 50px
    } else {
      this.bgColor = 'transparent'; // Change background color back to transparent
    }
  }


  cartItemsCount() {
    let cartData = localStorage.getItem("MyItems");
    if (cartData) {
      this.cartCount = JSON.parse(cartData).length;
    }
    this.itemSer.cardData.subscribe((item) => {
      this.cartCount = item.length;
    })
  }


  checkUser() {

   this.itemSer.ActiveUser.subscribe((res)=>{
     this.ActiveUser = res;
    })

  }



  checkSellerActive(){
this.sellerSer.sellerActive.subscribe((res:any)=>{
  this.sellerActive = res;
})
  }


  emitUser() {
    let user = localStorage.getItem("user");
    let userVerify = user && JSON.parse(user).data.id
    if(userVerify){
   this.itemSer.ActiveUser.emit(true);
  }
  }


  logOut(){
    localStorage.removeItem("user");
    localStorage.removeItem("seller");
    this.sellerSer.sellerActive.emit(false);
    this.itemSer.ActiveUser.emit(false);
    this.toaster.LogOutSuccess();
    this.route.navigate(['/login'])
  }

  checkSeller(){
    let seller = localStorage.getItem("seller");
    let sellerVerify = seller && JSON.parse(seller).data.id
    if(sellerVerify){
   this.sellerSer.sellerActive.emit(true);
  }
  }


}
<nav class="navbar navbar-expand-lg sticky-top  " [ngStyle]="{'background-color': bgColor}">
  <div class="container-fluid">
    <a class="navbar-brand" routerLink={{sellerActive}}>FoodCounter</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup"
      aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
      <i class="fas fa-bars"></i>
    </button>
    <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
      <div class="navbar-nav">
        <a class="nav-link active" aria-current="page" [routerLink]="">Home</a>
        <a class="nav-link" *ngIf="!sellerActive" routerLink="cart">Cart({{ cartCount }})</a>
        <a class="nav-link" *ngIf="sellerActive" routerLink="addProduct">Add Product</a>
        <a class="nav-link"  routerLink="about">About</a>
        <a class="nav-link" *ngIf="!sellerActive" routerLink="myOrder">My orders</a>
        <a class="nav-link"  *ngIf="ActiveUser || sellerActive"  routerLink="profile">Profile</a>
        <a class="nav-link" *ngIf="!sellerActive && !ActiveUser" routerLink="seller">Seller</a>

        <a class="nav-link  " *ngIf="!ActiveUser && !sellerActive" routerLink="login">Login</a>
        <a class="nav-link  " *ngIf="ActiveUser || sellerActive" (click)="logOut()" style="cursor: pointer;">Logout</a>
      </div>
    </div>
  </div>
</nav>
