import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { SellerService } from 'src/app/services/seller.service';

@Component({
  selector: 'app-seller-add-product',
  templateUrl: './seller-add-product.component.html',
  styleUrls: ['./seller-add-product.component.scss']
})
export class SellerAddProductComponent {
  sellerAddProduct : FormGroup;

  constructor(private fb:FormBuilder, private sellerSer:SellerService){
    this.sellerAddProduct = this.fb.group({
      item_name : this.fb.control('',[Validators.required]),
      item_price : this.fb.control('',[Validators.required]),
      item_discount : this.fb.control('',[Validators.required]),
      item_qty : this.fb.control('',[Validators.required]),
      item_image : this.fb.control('',[Validators.required]),
      item_dsc : this.fb.control('',[Validators.required]),
    })
  }

  ngOnInit(): void {
    }


    addProduct(value:any){
if(this.sellerAddProduct.valid){
this.sellerSer.addProduct(value)

}

    }
}
<div class="container">
  <div class="row d-flex justify-content-center">
    <div class="col-6 mt-5 mb-5">
      <form [formGroup]="sellerAddProduct" (ngSubmit)="addProduct(sellerAddProduct.value)">

        <div class="formInput mt-2 mb-2">
          <label for="">Item Name</label>
          <input type="text" formControlName="item_name" id="" class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Price</label>
          <input type="text" formControlName="item_price" id="" class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Description</label>
          <input type="text" formControlName="item_dsc" id="" class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Quantity</label>
          <input type="number" formControlName="item_qty" id="" class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Image URL</label>
          <input type="text" formControlName="item_image" id="" class="form-control">
        </div>

        <div class="formInput mt-2 mb-2">
          <label for="">Discount</label>
          <input type="number" formControlName="item_discount" id="" class="form-control">
        </div>

        <div class="loginBtn mt-4 d-flex justify-content-between">
          <button class="btn btn-dark col-4 col-sm-4">Add Product</button>

        </div>
      </form>
    </div>
  </div>
</div>
