import { Component, OnInit } from '@angular/core';
import { SellerService } from 'src/app/services/seller.service';

@Component({
  selector: 'app-seller-dashboard',
  templateUrl: './seller-dashboard.component.html',
  styleUrls: ['./seller-dashboard.component.scss']
})
export class SellerDashboardComponent implements OnInit {
itemList=[{
  item_image : 'https://static.toiimg.com/thumb/61050397.cms?imgsize=246859&width=800&height=800', item_name: 'Samosa', item_discount: '5', item_price: '15'
}]

constructor(private sellerSer:SellerService,priv ){}

ngOnInit(): void {
  this.checkSeller();
  this.getItems();
}






checkSeller(){
  let seller = localStorage.getItem("seller");
  let sellerVerify = seller && JSON.parse(seller).data.id
  if(sellerVerify){
 this.sellerSer.sellerActive.emit(true);
}
}



getItems() {
  this.IS.getItems().subscribe((res: any) => {
    this.itemList = res.Result;
  })
}
}
